    } else {
      setChecklastName(false);
    }
  };
  const setaddress1 = (event) => {
    setAddress1(event);
    if (event == "") {
      setCheckAddress1(true);
    } else {
      setCheckAddress1(false);
    }
  };
  const setaddress2 = (event) => {
    setAddress2(event);
  };
  const setcity = (event) => {
    setCity(event);
    if (event == "") {
      setCheckCity(true);
    } else {
      setCheckCity(false);
    }
  };
  const setstate = (event) => {
    setState(event);
    if (event == "") {
      setCheckState(true);
    } else {
      setCheckState(false);
    }
  };
  const setzipCode = (event) => {
    setZipCode(event);
    if (event == "") {
      setCheckZipCode(true);
    } else {
      setCheckZipCode(false);
    }
  };

  const setemail = (event) => {
    setEmail(event);
    if (event == "") {
      setCheckEmail(true);
    } else {
      setCheckEmail(false);
    }
  };
  const setsecondaryemail = (event) => {
    setSecondaryemail(event);
  };

  const setphone1 = (event) => {
    setPhone1(event);
    if (event == "") {
      setCheckPhone1(true);
    } else {
      setCheckPhone1(false);
    }
  };
  const setphone2 = (event) => {
    setPhone2(event);
  };
  const setphone3 = (event) => {
    setPhone3(event);
  };
  const setemployer = (event) => {
    setEmployer(event);
    if (event == "") {
      setCheckEmployer(true);
    } else {
      setCheckEmployer(false);
    }
  };
  const setoccupation = (event) => {
    setOccupation(event);
    if (event == "") {
      setCheckOccupation(true);
    } else {
      setCheckOccupation(false);
    }
  };
  const setnextURL = (event) => {
    setNextURL(event);
    if (event == "") {
      setCheckNextURL(true);
    } else {
      setCheckNextURL(false);
    }
  };
  //Form Submission
  const submitForm = () => {
    setErrorMessage('')
    setSuccessMessage('')
    if (address1 == "") {
      setCheckAddress1(true);
      return false;
    }

    if (firstName == "") {
      setCheckFirstname(true);
      return false;
    }
    if (lastName == "") {
      setChecklastName(true)
      return false;
    }
    if (email == "") {
      setCheckEmail(true);
      return false;
    }
    if (phone1 == "") {
      setCheckPhone1(true);
      return false;
    }
    if (employer == "") {
      setCheckEmployer(true);
      return false;
    }
    if (occupation == "") {
      setCheckOccupation(true);
      return false;
    }
    if (nextURL == "") {
      setCheckNextURL(true);
      return false;
    }
    console.log(API_URL);
    console.log('here');
    fetch(`${API_URL}/Connect/RecordInquiry`, {
      method: "post",
      headers: {
        Accept: "*/*",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        "SchoolGuid": "29D2568F-5950-46A6-A54E-DDA0BD0ADFD4",
        "FirstName": firstName,
        "LastName": lastName,
        "FullName": firstName + ' ' + lastName,
        "Email": email,
        "Phone1": phone1,
        "Comments": comments,
        "Source": source,
        "NextURL": nextURL,
        "IsFromMobile": "true"
      }),
    })
      .then((response) => {
        let jsonData = JSON.stringify(response);
        console.log(jsonData)
        let jsonDataPrase = JSON.parse(jsonData);
        console.log(jsonDataPrase.status)
        if (jsonDataPrase.status != 200) {
          setErrorMessage("An error has occurred.");
        } else {
          setSuccessMessage('Successfully Submitted.')
          // props.navigation.navigate("ResetSuccess");
        }
      })
      .catch((response) => {
        setErrorMessage("An error has occurred.");
      });
  };
  const { navigation } = props;
  return (
    <Container style={loginStyle.container}>
      <SideBarMenu title={"Inquiry "} navigation={props.navigation} />
      <Content style={loginStyle.spacing} padder>
        <View style={loginStyle.backWrapper}>
          {/* <Image
            style={loginStyle.backButton}
            source={require("../../../assets/BackButton.png")}
          /> */}
        </View>
        <Body style={loginStyle.bodyContainer}>
          <H2 style={globalStyle.h2}>Inquiry!</H2>
          <Text style={globalStyle.small}>Fill out the form below </Text>
        </Body>
        <Form>
          <View style={{
            shadowColor: "#000",
            shadowOffset: {
              width: 0,
              height: 2,
            },
            shadowOpacity: 0.25,
            shadowRadius: 3.84,
            elevation: 5,
            backgroundColor: "#f7f7f7",
            marginTop: 30,
            padding: 15,
            paddingBottom: 30
          }}>
            <Text style={{ color: "#000", fontSize: 24, fontWeight: "bold", marginBottom: 0 }}>Personal Information</Text>
            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={firstName}
                onChangeText={(text) => setfirstName(text)}
                style={
                  checkFirstname
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="First Name"
              />
            </Item>
            {checkFirstname ? (
              <Text style={globalStyle.error}>Enter First Name</Text>
            ) : null}
            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={lastName}
                onChangeText={(text) => setlasttName(text)}
                style={globalStyle.formControl}
                placeholder="Last Name"
              />
            </Item>
            {checklastName ? (
              <Text style={globalStyle.error}>Enter Last Name </Text>
            ) : null}
            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={address1}
                onChangeText={(text) => setaddress1(text)}
                style={
                  checkAddress1
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Address1"
              />
            </Item>
            {checkAddress1 ? (
              <Text style={globalStyle.error}>Enter Address </Text>
            ) : null}
            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={address2}
                onChangeText={(text) => setaddress2(text)}
                style={globalStyle.formControl}
                placeholder="Address2"
              />
            </Item>

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={city}
                onChangeText={(text) => setcity(text)}
                style={
                  checkCity
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="City "
              />
            </Item>
            {checkCity ? (
              <Text style={globalStyle.error}>Enter City</Text>
            ) : null}

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={state}
                onChangeText={(text) => setstate(text)}
                style={
                  checkState
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="State "
              />
            </Item>
            {checkState ? (
              <Text style={globalStyle.error}>Enter State</Text>
            ) : null}

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={zipCode}
                onChangeText={(text) => setzipCode(text)}
                style={
                  checkZipCode
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Postal Code"
              />
            </Item>
            {checkZipCode ? (
              <Text style={globalStyle.error}>Enter ZipCode</Text>
            ) : null}

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={email}
                onChangeText={(text) => setemail(text)}
                style={
                  checkEmail
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Primary Email "
              />
            </Item>
            {checkEmail ? (
              <Text style={globalStyle.error}>Enter Valid Email</Text>
            ) : null}
            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={secondaryemail}
                onChangeText={(text) => setsecondaryemail(text)}
                style={globalStyle.formControl}
                placeholder="Secondary Email "
              />
            </Item>

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={phone1}
                onChangeText={(text) => setphone1(text)}
                style={
                  checkPhone1
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Phone1"
              />
            </Item>
            {checkPhone1 ? (
              <Text style={globalStyle.error}>Enter Phone Number </Text>
            ) : null}

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={phone2}
                onChangeText={(text) => setphone2(text)}
                style={globalStyle.formControl}
                placeholder="Phone2"
              />
            </Item>

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={phone3}
                onChangeText={(text) => setphone3(text)}
                style={globalStyle.formControl}
                placeholder="Phone3"
              />
            </Item>

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={employer}
                onChangeText={(text) => setemployer(text)}
                style={
                  checkEmployer
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Employer"
              />
            </Item>
            {checkEmployer ? (
              <Text style={globalStyle.error}>Enter Employement </Text>
            ) : null}

            <Item style={globalStyle.formGroup} floatingLabel>
              <Input
                value={occupation}
                onChangeText={(text) => setoccupation(text)}
                style={
                  checkOccupation
                    ? globalStyle.formControlError
                    : globalStyle.formControl
                }
                placeholder="Occupation"
              />
            </Item>
            {checkOccupation ? (
              <Text style={globalStyle.error}>Enter Occupation </Text>
            ) : null}
          </View>
          {/* <Item style={globalStyle.formGroup} floatingLabel>
            <Input
              value={InquiryTypeId}
