import React from "react";
import { View, Image, StyleSheet } from "react-native";
import {
  Container,
  Content,
  Form,
  Item,
  Input,
  Label,
  Button,
  Text,
  Body,
  H3,
  Icon,
} from "native-base";
import loginStyle from "../../style/login/loginStyle";
import globalStyle from "../../style/globalStyle";
import CheckBox from "@react-native-community/checkbox";
import { API_URL } from "@env"

import PhoneInput from 'react-phone-number-input/react-native-input'

const SignUp = (props) => {
  const [firstName, setFirstName] = React.useState("");
  const [lastName, setLastName] = React.useState("");
  const [schoolId, setSchoolId] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [confirmEmail, setConfirmEmail] = React.useState("");
  const [mobile, setMobile] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [terms, setTerms] = React.useState(false);
  const [checkFirstname, setCheckFirstname] = React.useState(false);
  const [checkShoolId, setCheckShoolId] = React.useState(false);
  const [checkEmail, setCheckEmail] = React.useState(false);
  const [checkConfirmEmail, setCheckConfirmEmail] = React.useState(false);
  const [checkPassword, setCheckPassword] = React.useState(false);
  const [checkmobile, setCheckmobile] = React.useState(false);
  const [checkterms, setCheckterms] = React.useState(false);
  const [errorMessage, setErrorMessage] = React.useState(false);
  const [accessToken, setAccessToken] = React.useState('');
  // Email validations custom
  const ValidateEmail = (mail) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(mail);
  };

  // Setting data to variables and validations
  const setfirstName = (event) => {
    setFirstName(event);
    if (event == "") {
      setCheckFirstname(true);
    } else {
      setCheckFirstname(false);
    }
  };
  const setlasttName = (event) => {
    setLastName(event);
  };
  const setschoolId = (event) => {
    setSchoolId(event);
    if (event == "") {
      setCheckShoolId(true);
    } else {
      setCheckShoolId(false);
    }
  };
  const setemail = (event) => {
    setEmail(event);
    if (event == "") {
      setCheckEmail(true);
    } else {
      setCheckEmail(false);
    }
  };
  const setconfirmemail = (event) => {
    setConfirmEmail(event);
    if (event == "") {
      setCheckConfirmEmail(true);
    } else {
      setCheckConfirmEmail(false);
    }
  };
  const setpassword = (event) => {
    setPassword(event);
    if (event == "") {
      setCheckPassword(true);
    } else {
      setCheckPassword(false);
    }
  };
  const setmobile = (event) => {
    setMobile(event);
    if (event == "") {
      setCheckmobile(true);
    } else {
      setCheckmobile(false);
    }
  };
  const setterms = () => {
    setTerms(!terms);
    if (terms == false) {
      setCheckterms(false);
    } else {
      setCheckterms(true);
    }
  };

  //Form Submission
  const submitForm = () => {
    if (firstName == "") {
      setCheckFirstname(true);
      return false;
    }
    if (schoolId == "") {
      setCheckShoolId(true);
      return false;
    }
    if (email == "") {
      setCheckEmail(true);
      return false;
    }
    let checkemail = ValidateEmail(email);
    if (checkemail == false) {
      setCheckEmail(true);
      return false;
    }

    if (confirmEmail == "") {
      setCheckConfirmEmail(true);
      return false;
    }
    let checkConfirmemail = ValidateEmail(confirmEmail);
    if (checkConfirmemail == false) {
      setCheckConfirmEmail(true);
      return false;
    }
    if (email != confirmEmail) {
      setCheckConfirmEmail(true);
      return false;
    }
    if (password == "") {
      setCheckPassword(true);
      return false;
    }
    if (mobile == "") {
      setCheckmobile(true);
      return false;
    }
    if (terms == false) {
      setCheckterms(true);
      return false;
    }
    function login() {
      var details = {
        userName: email,
        password: password,
        grant_type: "password",
      };

      var formBody = [];
      for (var property in details) {
        var encodedKey = encodeURIComponent(property);
        var encodedValue = encodeURIComponent(details[property]);
        formBody.push(encodedKey + "=" + encodedValue);
      }
      formBody = formBody.join("&");

      fetch(`${API_URL}/token`, {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
        },
        body: formBody,
      })
        .then((response) => response.json())
        .then((response) => {
          console.log(response["access_token"]); 
          setAccessToken(response["access_token"])
          linkStudent() 
        })
        .catch(function (data) {
          props.navigation.navigate("AccountSuccess");
        });
    }

    function linkStudent() {
      fetch(`${API_URL}/odata/StudentLink`, {
        method: "post",
        headers: {
          Accept: "*/*",
          "Content-Type": "application/json",
          'Authorization': 'Bearer ' + accessToken
        },
        body: JSON.stringify({
          Email: email,
          FirstName: firstName,
          LastName: lastName,
        }),
      })
        .then((response) => response.json())
        .then((response) => {
          console.log(response);
          if (response["odata.error"]) {
            props.navigation.navigate("AccountSuccess");
            console.log(response["odata.error"].message.value);
            setErrorMessage(response["odata.error"].message.value);
          } else {
            props.navigation.navigate("VerificationLinkStudentSignup", {
              accessToken: accessToken,
              studentAccountGuid: response["studentAccountGuid"],
              studentId: response["studentId"],
              Email: email,
              FirstName: firstName,
              LastName: lastName,
            });
          }
        })
        .catch((response) => {
          props.navigation.navigate("AccountSuccess");
        });
    }
    fetch(`${API_URL}/odata/Register`, {
      method: "post",
      headers: {
        Accept: "*/*",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        Email: email,
        EmailConfirmed: confirmEmail,
        Password: password,
        SchoolUniqueId: schoolId,
        FirstName: firstName,
        LastName: lastName,
        WaiverFile: "some text",
        Phone1: mobile,
      }),
    })
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        if (response["odata.error"]) {
          console.log(response["odata.error"].message.value);
          setErrorMessage(response["odata.error"].message.value);
        } else {
          login();
         props.navigation.navigate("AccountSuccess");
        }
      })
      .catch((response) => {
        setErrorMessage("An error has occurred. Please check all the fields");
      });
  };

  const { navigation } = props.navigation;
  return (
