            />
            <Text style={loginStyle.text}>
              I agree to the Terms and Conditions
            </Text>
          </View>
          {checkterms ? (
            <Text style={globalStyle.error}>
              Please agree to the Terms and Conditions before proceed
            </Text>
          ) : null}
          {errorMessage != "" ? (
            <Text style={globalStyle.errorText}>{errorMessage}</Text>
